defmodule AbiEncodePackedTest do
  use ExUnit.Case

  # Corresponds to message from block 200113
  describe "encode_packed_mixed/1" do
    test "encodes a message for tx" do
      encoded =
        Abi.encode_packed_mixed([
          "0x073314940630fd6dcda0d772d4c972c4e0a9946bef9dabf4ef84eda8ef542b82",
          "0xae0ee0a63a2ce6baeeffe56e7714fb4efe48d419",
          4,
          ["0x00", "0xa3690506bbd48eea3c8e76836afd7974af2c6282", "0x0cc47f20295c0000", "0x00"]
        ])

      assert Base.encode16(encoded) ==
                 ""
    end
  end
end
